mapper.select:
select plan.*, (select count(*) from plan_good where plan_no = plan.idx) good
from plan
where idx = #{idx}


select *
from (
         SELECT a.*, ROWNUM as rnum
         FROM (
                  select plan.*, (select count(*) from plan_good where plan_no = plan.idx) good
                  from plan
                  where pub = 1
                  order by idx desc
              ) a
     )
WHERE rnum >= #{startRow}
  and rnum <= #{endRow}

select count(*)
from plan_good
where plan_no = #{idx}
  and username = #{username}

select count(*)
from plan_good
where plan_no = #{idx}

mapper.update:
update plan
set title=#{title},
    days=#{days},
    plans=#{plans}
where idx = #{idx}

update plan
set pub=1
where idx = #{idx}

update plan
set pub=0
where idx = #{idx}

update plan
set hit = hit + 1
where idx = #{idx}

mapper.delete:
delete
from plan_good
where plan_no = #{idx}
  and username = #{username}
